     CREATE TABLE history_wallets (guid CHAR(36) NOT NULL COMMENT 'Дата и время создания записи(DC2Type:guid)', guid_client CHAR(36) NOT NULL COMMENT 'Имя пользователя', name_client VARCHAR(255) NOT NULL COMMENT 'Имя пользователя', amount NUMERIC(15, 4) NOT NULL COMMENT 'Списанная/добавленная сумма в основной валюте', currency_code CHAR(3) DEFAULT 'USD' NOT NULL COMMENT 'Код валюты', currency_amount NUMERIC(15, 4) NOT NULL COMMENT 'Списанная/добавленная сумма в валюте кошелька', created_at VARCHAR(255) DEFAULT 'CURRENT_TIMESTAMP' NOT NULL COMMENT 'Дата и время создания записи', comment VARCHAR(255) DEFAULT NULL COMMENT 'Комментарий к операции', INDEX guid_client_idx (guid_client), INDEX created_at_idx (created_at), PRIMARY KEY(guid)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = 'История операция по кошельку клиента' ;
     CREATE TABLE currencies (code CHAR(3) NOT NULL COMMENT 'Буквенный код валюты', name CHAR(36) NOT NULL COMMENT 'Название валюты', created_at DATETIME NOT NULL COMMENT 'Дата и время создания валюты', PRIMARY KEY(code)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = 'Валюты системы' ;
     CREATE TABLE wallets (client_guid CHAR(36) NOT NULL COMMENT 'Идентификатор клиента(DC2Type:guid)', currency_code CHAR(3) DEFAULT NULL COMMENT 'Буквенный код валюты', balance NUMERIC(15, 4) NOT NULL COMMENT 'Денежный остаток', created_at DATETIME NOT NULL COMMENT 'Дата и время создания кошелька', INDEX IDX_967AAA6CFDA273EC (currency_code), PRIMARY KEY(client_guid)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = 'Таблица <<кошелёк>> клиента' ;
     CREATE TABLE clients (guid CHAR(36) NOT NULL COMMENT 'Идентификатор клиента(DC2Type:guid)', name VARCHAR(255) NOT NULL COMMENT 'Имя пользователя', country VARCHAR(255) NOT NULL COMMENT 'Страна регистрации', city VARCHAR(255) NOT NULL COMMENT 'Город регистрации', created_at DATETIME NOT NULL COMMENT 'Дата и время создания клиента', UNIQUE INDEX uniq_name_country_city (name, country, city), PRIMARY KEY(guid)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = 'Клиенты системы' ;
     CREATE TABLE exchange_rates (guid CHAR(36) NOT NULL COMMENT 'Идентификатор курса(DC2Type:guid)', currency_code CHAR(3) DEFAULT NULL COMMENT 'Буквенный код валюты', rate NUMERIC(9, 7) NOT NULL COMMENT 'Курс валюты по отношению к USD', date DATE NOT NULL COMMENT 'Дата действия курса', created_at DATETIME NOT NULL COMMENT 'Дата и время установки курса', INDEX IDX_5AE3E774FDA273EC (currency_code), UNIQUE INDEX uniq_currency_date (currency_code, date), PRIMARY KEY(guid)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB COMMENT = 'Курсы валют' ;
